import React, { useState, useEffect } from 'react';
import {
  User,
  Settings,
  Play,
  Pause,
  CheckCircle,
  XCircle,
  Clock,
  Eye,
  Edit,
  Trash2,
  Plus,
  Search,
  Filter,
  Bell,
  Menu,
  X,
  Home,
  BarChart3,
  FileText,
  Loader2,
  AlertCircle,
  CheckCheck,
  Info
} from 'lucide-react';

const RPAPlatform = () => {
  const [isLoggedIn, setIsLoggedIn] = useState(false);
  const [currentUser, setCurrentUser] = useState(null);
  const [activeTab, setActiveTab] = useState('dashboard');
  const [tasks, setTasks] = useState([]);
  const [sidebarOpen, setSidebarOpen] = useState(true);
  const [notifications, setNotifications] = useState([]);
  const [loading, setLoading] = useState(false);
  const [toast, setToast] = useState(null);

  // 显示提示信息
  const showToast = (message, type = 'info') => {
    setToast({ message, type, id: Date.now() });
    setTimeout(() => setToast(null), 3000);
  };

  // 模拟登录
  const handleLogin = async (username, password) => {
    setLoading(true);
    await new Promise(resolve => setTimeout(resolve, 1000));

    if (username && password) {
      setCurrentUser({
        name: username,
        role: username === 'admin' ? 'admin' : 'user',
        permissions: username === 'admin' ? ['all'] : ['basic']
      });
      setIsLoggedIn(true);
      showToast('登录成功！', 'success');
      loadTasks();
    } else {
      showToast('用户名或密码不能为空', 'error');
    }
    setLoading(false);
  };

  // 模拟数据加载
  const loadTasks = async () => {
    setLoading(true);
    await new Promise(resolve => setTimeout(resolve, 500));

    const mockTasks = [
      {
        id: 1,
        name: 'Amazon产品详情抓取',
        platform: 'amazon',
        status: 'running',
        progress: 75,
        lastRun: '2025-06-04 14:30',
        nextRun: '2025-06-04 15:00',
        description: '自动抓取Amazon产品详情页面信息',
        createdBy: 'admin'
      },
      {
        id: 2,
        name: 'Amazon库存监控',
        platform: 'amazon',
        status: 'completed',
        progress: 100,
        lastRun: '2025-06-04 14:00',
        nextRun: '2025-06-04 16:00',
        description: '监控Amazon库存变化情况',
        createdBy: 'user'
      }
    ];
    setTasks(mockTasks);
    setLoading(false);
  };

  // Toast组件
  const Toast = () => {
    if (!toast) return null;

    const icons = {
      success: CheckCheck,
      error: AlertCircle,
      info: Info
    };

    const colors = {
      success: 'bg-green-500',
      error: 'bg-red-500',
      info: 'bg-blue-500'
    };

    const Icon = icons[toast.type];

    return (
      <div className="fixed top-4 right-4 z-50">
        <div className={`${colors[toast.type]} text-white px-4 py-3 rounded-lg shadow-lg flex items-center space-x-2`}>
          <Icon className="w-5 h-5" />
          <span>{toast.message}</span>
          <button onClick={() => setToast(null)} className="ml-2">
            <X className="w-4 h-4" />
          </button>
        </div>
      </div>
    );
  };

  // 登录组件
  const LoginForm = () => {
    const [username, setUsername] = useState('');
    const [password, setPassword] = useState('');

    const handleSubmit = (e) => {
      e.preventDefault();
      handleLogin(username, password);
    };

    return (
      <div className="min-h-screen bg-gradient-to-br from-blue-50 to-indigo-100 flex items-center justify-center p-4">
        <div className="bg-white rounded-xl shadow-2xl p-8 w-full max-w-md">
          <div className="text-center mb-8">
            <div className="w-16 h-16 bg-blue-600 rounded-full flex items-center justify-center mx-auto mb-4">
              <span className="text-white font-bold text-xl">龙</span>
            </div>
            <h1 className="text-3xl font-bold text-gray-900 mb-2">龙行RPA</h1>
            <p className="text-gray-600">电商自动化管理平台</p>
          </div>

          <form onSubmit={handleSubmit}>
            <div className="space-y-4">
              <div>
                <label className="block text-sm font-medium text-gray-700 mb-1">用户名</label>
                <input
                  type="text"
                  value={username}
                  onChange={(e) => setUsername(e.target.value)}
                  className="w-full px-3 py-2 border border-gray-300 rounded-md focus:outline-none focus:ring-2 focus:ring-blue-500"
                  placeholder="请输入用户名"
                  disabled={loading}
                />
              </div>
              <div>
                <label className="block text-sm font-medium text-gray-700 mb-1">密码</label>
                <input
                  type="password"
                  value={password}
                  onChange={(e) => setPassword(e.target.value)}
                  className="w-full px-3 py-2 border border-gray-300 rounded-md focus:outline-none focus:ring-2 focus:ring-blue-500"
                  placeholder="请输入密码"
                  disabled={loading}
                />
              </div>
              <button
                type="submit"
                disabled={loading}
                className="w-full bg-blue-600 text-white py-2 px-4 rounded-md hover:bg-blue-700 transition-colors disabled:opacity-50 flex items-center justify-center"
              >
                {loading ? (
                  <>
                    <Loader2 className="w-4 h-4 mr-2 animate-spin" />
                    登录中...
                  </>
                ) : (
                  '登录'
                )}
              </button>
            </div>
          </form>

          <div className="mt-6 text-center text-sm text-gray-500">
            <p>测试账号: admin/admin 或 user/user</p>
          </div>
        </div>
      </div>
    );
  };

  // 任务状态指示器
  const StatusIndicator = ({ status, progress }) => {
    const statusConfig = {
      running: { icon: Loader2, color: 'text-blue-500', bg: 'bg-blue-100', text: '运行中' },
      completed: { icon: CheckCircle, color: 'text-green-500', bg: 'bg-green-100', text: '已完成' },
      failed: { icon: XCircle, color: 'text-red-500', bg: 'bg-red-100', text: '失败' },
      pending: { icon: Clock, color: 'text-yellow-500', bg: 'bg-yellow-100', text: '待处理' }
    };

    const config = statusConfig[status] || statusConfig.pending;
    const Icon = config.icon;

    return (
      <div className="flex items-center space-x-2">
        <div className={`p-1 rounded-full ${config.bg}`}>
          <Icon className={`w-4 h-4 ${config.color} ${status === 'running' ? 'animate-spin' : ''}`} />
        </div>
        <span className="text-sm font-medium">{config.text}</span>
      </div>
    );
  };

  // 通知面板
  const NotificationPanel = () => {
    const [isOpen, setIsOpen] = useState(false);

    return (
      <div className="relative">
        <button
          onClick={() => setIsOpen(!isOpen)}
          className="text-gray-500 hover:text-gray-700"
        >
          <Bell className="w-6 h-6" />
        </button>

        {isOpen && (
          <div className="absolute right-0 mt-2 w-80 bg-white rounded-lg shadow-lg border z-50">
            <div className="p-4">
              <h3 className="font-semibold">通知</h3>
              <p className="text-gray-500 text-sm mt-2">暂无新通知</p>
            </div>
          </div>
        )}
      </div>
    );
  };

  // 工作流市场
  const AmazonMarketplace = () => {
    const [selectedCategory, setSelectedCategory] = useState('运营精选');

    const categories = [
      { id: '运营精选', name: '运营精选', color: 'bg-purple-600' },
      { id: '数据分析', name: '数据分析', color: 'bg-blue-600' },
      { id: '库存管理', name: '库存管理', color: 'bg-green-600' },
      { id: '客户服务', name: '客户服务', color: 'bg-orange-600' }
    ];

    const workflows = [
      {
        id: 1,
        title: '产品详情页明细',
        subtitle: '自动与访问量-报告asin',
        description: '历史每天报表数据、一键开通详情页面',
        icon: '📊',
        category: '数据下载'
      },
      {
        id: 2,
        title: '店铺健康状况汇总',
        subtitle: 'Account Health',
        description: '自动检测店铺的健康状况汇总报表',
        icon: '🏥',
        category: '数据监控'
      }
    ];

    return (
      <div className="space-y-6">
        <div className="bg-gradient-to-r from-purple-900 to-blue-900 rounded-lg p-6 text-white">
          <h2 className="text-2xl font-bold mb-2">Amazon RPA 工作流市场</h2>
          <p className="text-purple-100">选择并配置您需要的自动化工作流程</p>
        </div>

        <div className="bg-white rounded-lg shadow p-4">
          <div className="flex space-x-4">
            {categories.map((category) => (
              <button
                key={category.id}
                onClick={() => setSelectedCategory(category.id)}
                className={`px-4 py-2 rounded-lg text-sm font-medium ${selectedCategory === category.id
                    ? `${category.color} text-white`
                    : 'bg-gray-100 text-gray-700'
                  }`}
              >
                {category.name}
              </button>
            ))}
          </div>
        </div>

        <div className="grid grid-cols-1 md:grid-cols-2 lg:grid-cols-3 gap-6">
          {workflows.map((workflow) => (
            <div key={workflow.id} className="bg-white rounded-lg shadow hover:shadow-lg transition-shadow p-6">
              <div className="flex items-start space-x-4">
                <div className="w-12 h-12 bg-orange-100 rounded-lg flex items-center justify-center">
                  <span className="text-2xl">{workflow.icon}</span>
                </div>
                <div className="flex-1">
                  <h3 className="text-lg font-semibold text-gray-900">{workflow.title}</h3>
                  <p className="text-sm text-gray-600 mb-2">{workflow.subtitle}</p>
                  <p className="text-sm text-gray-500 mb-4">{workflow.description}</p>
                  <div className="flex items-center justify-between">
                    <span className="bg-orange-100 text-orange-800 text-xs px-2 py-1 rounded-full">
                      {workflow.category}
                    </span>
                    <button className="bg-orange-500 text-white px-4 py-2 rounded-md text-sm hover:bg-orange-600">
                      运行
                    </button>
                  </div>
                </div>
              </div>
            </div>
          ))}
        </div>
      </div>
    );
  };

  // 任务管理
  const TaskTable = () => {
    return (
      <div className="bg-white rounded-lg shadow overflow-hidden">
        <div className="px-6 py-4 border-b">
          <div className="flex items-center justify-between">
            <h3 className="text-lg font-semibold">任务管理</h3>
            <button className="bg-blue-600 text-white px-4 py-2 rounded-md text-sm hover:bg-blue-700 flex items-center">
              <Plus className="w-4 h-4 mr-1" />
              新建任务
            </button>
          </div>
        </div>

        <div className="overflow-x-auto">
          <table className="w-full">
            <thead className="bg-gray-50">
              <tr>
                <th className="px-6 py-3 text-left text-xs font-medium text-gray-500 uppercase">任务名称</th>
                <th className="px-6 py-3 text-left text-xs font-medium text-gray-500 uppercase">状态</th>
                <th className="px-6 py-3 text-left text-xs font-medium text-gray-500 uppercase">上次运行</th>
                <th className="px-6 py-3 text-left text-xs font-medium text-gray-500 uppercase">操作</th>
              </tr>
            </thead>
            <tbody className="divide-y divide-gray-200">
              {tasks.map((task) => (
                <tr key={task.id} className="hover:bg-gray-50">
                  <td className="px-6 py-4">
                    <div className="text-sm font-medium text-gray-900">{task.name}</div>
                    <div className="text-sm text-gray-500">{task.description}</div>
                  </td>
                  <td className="px-6 py-4">
                    <StatusIndicator status={task.status} progress={task.progress} />
                  </td>
                  <td className="px-6 py-4 text-sm text-gray-500">{task.lastRun}</td>
                  <td className="px-6 py-4">
                    <div className="flex space-x-2">
                      <button className="text-blue-600 hover:text-blue-900">
                        <Play className="w-4 h-4" />
                      </button>
                      <button className="text-gray-600 hover:text-gray-900">
                        <Edit className="w-4 h-4" />
                      </button>
                      <button className="text-red-600 hover:text-red-900">
                        <Trash2 className="w-4 h-4" />
                      </button>
                    </div>
                  </td>
                </tr>
              ))}
            </tbody>
          </table>
        </div>
      </div>
    );
  };

  // 仪表板
  const Dashboard = () => {
    const stats = [
      { name: '运行中任务', value: '2', icon: Play, color: 'text-blue-600' },
      { name: '已完成任务', value: '8', icon: CheckCircle, color: 'text-green-600' },
      { name: '失败任务', value: '1', icon: XCircle, color: 'text-red-600' },
      { name: '待处理任务', value: '3', icon: Clock, color: 'text-yellow-600' }
    ];

    return (
      <div className="space-y-6">
        <div className="grid grid-cols-1 md:grid-cols-2 lg:grid-cols-4 gap-4">
          {stats.map((stat) => (
            <div key={stat.name} className="bg-white rounded-lg shadow p-6">
              <div className="flex items-center">
                <div className="flex-shrink-0">
                  <stat.icon className={`w-8 h-8 ${stat.color}`} />
                </div>
                <div className="ml-4">
                  <p className="text-sm font-medium text-gray-500">{stat.name}</p>
                  <p className="text-2xl font-semibold text-gray-900">{stat.value}</p>
                </div>
              </div>
            </div>
          ))}
        </div>

        <TaskTable />
      </div>
    );
  };

  // 用户管理
  const UserManagement = () => {
    return (
      <div className="bg-white rounded-lg shadow p-6">
        <h3 className="text-lg font-semibold mb-4">用户管理</h3>
        <p className="text-gray-500">用户管理功能开发中...</p>
      </div>
    );
  };

  // 系统设置
  const SystemSettings = () => {
    return (
      <div className="bg-white rounded-lg shadow p-6">
        <h3 className="text-lg font-semibold mb-4">系统设置</h3>
        <p className="text-gray-500">系统设置功能开发中...</p>
      </div>
    );
  };

  // 侧边栏
  const Sidebar = () => {
    const menuItems = [
      { id: 'dashboard', name: '仪表板', icon: Home },
      { id: 'amazon', name: 'Amazon工作流', icon: BarChart3 },
      { id: 'tasks', name: '任务管理', icon: Play },
      { id: 'users', name: '用户管理', icon: User, adminOnly: true },
      { id: 'settings', name: '系统设置', icon: Settings, adminOnly: true }
    ];

    return (
      <div className={`bg-gray-900 text-white ${sidebarOpen ? 'w-64' : 'w-16'} transition-all duration-300 flex flex-col`}>
        <div className="p-4">
          <div className="flex items-center space-x-2">
            <div className="w-8 h-8 bg-blue-600 rounded-lg flex items-center justify-center">
              <span className="text-white font-bold">龙</span>
            </div>
            {sidebarOpen && <span className="font-semibold">龙行RPA</span>}
          </div>
        </div>

        <nav className="flex-1 px-2 space-y-1">
          {menuItems.map((item) => {
            if (item.adminOnly && currentUser?.role !== 'admin') return null;

            return (
              <button
                key={item.id}
                onClick={() => setActiveTab(item.id)}
                className={`w-full flex items-center px-2 py-2 text-sm font-medium rounded-md transition-colors ${activeTab === item.id
                    ? 'bg-gray-800 text-white'
                    : 'text-gray-300 hover:bg-gray-700 hover:text-white'
                  }`}
              >
                <item.icon className="w-5 h-5" />
                {sidebarOpen && <span className="ml-3">{item.name}</span>}
              </button>
            );
          })}
        </nav>
      </div>
    );
  };

  // 主内容渲染
  const renderContent = () => {
    switch (activeTab) {
      case 'dashboard':
        return <Dashboard />;
      case 'amazon':
        return <AmazonMarketplace />;
      case 'tasks':
        return <TaskTable />;
      case 'users':
        return <UserManagement />;
      case 'settings':
        return <SystemSettings />;
      default:
        return <Dashboard />;
    }
  };

  if (!isLoggedIn) {
    return <LoginForm />;
  }

  return (
    <div className="h-screen bg-gray-100 flex">
      <Sidebar />

      <div className="flex-1 flex flex-col overflow-hidden">
        <header className="bg-white shadow-sm border-b">
          <div className="flex items-center justify-between px-6 py-4">
            <div className="flex items-center space-x-4">
              <button
                onClick={() => setSidebarOpen(!sidebarOpen)}
                className="text-gray-500 hover:text-gray-700"
              >
                <Menu className="w-6 h-6" />
              </button>
              <h1 className="text-xl font-semibold text-gray-900">
                {activeTab === 'dashboard' && '仪表板'}
                {activeTab === 'amazon' && 'Amazon工作流市场'}
                {activeTab === 'tasks' && '任务管理'}
                {activeTab === 'users' && '用户管理'}
                {activeTab === 'settings' && '系统设置'}
              </h1>
            </div>

            <div className="flex items-center space-x-4">
              <NotificationPanel />
              <div className="flex items-center space-x-3">
                <span className="text-sm text-gray-700">欢迎, {currentUser?.name}</span>
                <button
                  onClick={() => {
                    setIsLoggedIn(false);
                    setCurrentUser(null);
                    setTasks([]);
                    showToast('已安全退出', 'info');
                  }}
                  className="text-red-600 hover:text-red-800 text-sm"
                >
                  登出
                </button>
              </div>
            </div>
          </div>
        </header>

        <main className="flex-1 overflow-auto p-6">
          {renderContent()}
        </main>
      </div>

      <Toast />
    </div>
  );
};

export default RPAPlatform;
